import React from 'react';
import PropTypes from 'prop-types';
import './style.css';

export default function  Card ({handleClick,  id, flipped, height, width}) {
    return (
        <div className={`flip-container  ${flipped ? 'flipped' : ''}`} style={{width, height}} onClick={()=>handleClick(id)}>
            <div className='flipper'>
                <img style={{height, width}} className={flipped ? 'front': 'back'}
                src={flipped ? `/img/${type}.png`  : `/img/back.png`}/>

            </div>
        </div>
    )
}


Card.propTypes = {   //här skriver vad är kraved om varje variabel
    handleClick:PropTypes.func.isRequired,
    id:PropTypes.number.isRequired,
    width:PropTypes.number.isRequired,
    height:PropTypes.number.isRequired,
    flipped:PropTypes.bool.isRequired,
    front:PropTypes.string.isRequired,
    back:PropTypes.string.isRequired
}
